import React, { Component , useState , useEffect} from 'react'

export class Form extends Component {
  state = {
    TDate : null,
    Hage : null,
    MRT : null,
    CStore : null,
    Latitude : null,
    Longitude : null
  };

  onSubmit = (e) => {
    e.preventDefault();
    
    const [TDate,Hage,MRT,CStore,Latitude,Longitude] = this.state;

    console.log(TDate);

    // APIService.InsertArticle({TDate,Hage,MRT,CStore,Latitude,Longitude})
    // .then((response) => props.insertedArticle(response))
    // .catch(error => console.log('error',error))
  };

  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value});
  };

  render() {
    return (
      <div>
          <h2 className='title'>
            {this.props.title}
          </h2>
          <form className='form' onSubmit={this.onSubmit}>
              <label for='TDate'>Transaction Date: </label>
              <input type='date' id='TDate' onChange={this.onChange} />
  
              <label for='Hage'>House age: </label>
              <input type='number' id='Hage' step='.01' placeholder='0' onChange={this.onChange} />
  
              <label for='MRT'>Distance to the nearest Metro Station(Metres): </label>
              <input type='number' id='MRT' step='0.00001' placeholder='0' onChange={this.onChange} />
  
              <label for='CStore'>Convinience Store: </label>
              <input type='number' id='CStore' placeholder='0' onChange={this.onChange} />
  
              <label for='Latitude'>Latitude: </label>
              <input type='number' id='Latitude' step='0.00001' placeholder='0' onChange={this.onChange} />
  
              <label for='Longitude'>Longitude: </label>
              <input type='number' id='Longitude' step='0.00001' placeholder='0' onChange={this.onChange} />
  
              <input type='submit'  value='Predict' className='submit' />
          </form>
          <form className='answer'>
            <label for='Prediction'>Predicted Price: </label>
            <input type='text' id='Prediction' value='' placeholder='Predicted Price will appear here'/>
          </form>
      </div>
    )
  }
}

export default Form
