import React, { useState } from 'react';



const Form = ({title}) => {
  const [TDate , setTDate] = useState({var : new Date()});
  const [Hage , setHage] = useState(0);
  const [MRT , setMRT] = useState(0);
  const [CStore , setCStore] = useState(0);
  const [Latitude , setLatitude] = useState(0);
  const [Longitude , setLongitude] = useState(0);


  const handleSubmit = (e) => {
    e.preventDefault();
  
    const data = {TDate , Hage , MRT , CStore , Latitude , Longitude};
  
    console.log(data);
  
    fetch('http://localhost:5000/predict', {
      method: 'POST',
      body: JSON.stringify({data}),
      headers: {'Content-type': 'application/json', 'Accept' : 'application/json'}
      })
      .then(response => response.json())
      .then(message => {console.log(message);})
      .catch(err => {console.log("Error Reading data " + err);});
  
  };

  return (
    <div>
        <h2 className='title'>
          {title}
        </h2>
        <form className='form' onSubmit={handleSubmit}>
            <label htmlFor='TDate'>Transaction Date: </label>
            <input type='date' id='TDate'  onChange={(e)=>setTDate(e.target.value)} />

            <label htmlFor='Hage'>House age: </label>
            <input type='number' id='Hage' step='.01' placeholder='0'  onChange={(e)=>setHage(e.target.value)} />

            <label htmlFor='MRT'>Distance to the nearest Metro Station(Metres): </label>
            <input type='number' id='MRT' step='0.00001' placeholder='0'  onChange={(e)=>setMRT(e.target.value)} />

            <label htmlFor='CStore'>Convinience Store: </label>
            <input type='number' id='CStore' placeholder='0'  onChange={(e)=>setCStore(e.target.value)} />

            <label htmlFor='Latitude'>Latitude: </label>
            <input type='number' id='Latitude' step='0.00001' placeholder='0'  onChange={(e)=>setLatitude(e.target.value)} />

            <label htmlFor='Longitude'>Longitude: </label>
            <input type='number' id='Longitude' step='0.00001' placeholder='0' value='5' onChange={(e)=>setLongitude(e.target.value)} />

            <input type='submit'  value='Predict' className='submit' />
        </form>
        <form className='answer'>
          <label htmlFor='Prediction'>Predicted Price: </label>
          <input type='text' id='Prediction' value='' placeholder='Predicted Price will appear here'/>
        </form>
    </div>
  ) 
}

export default Form
