import React, { Component , useState } from 'react'

export class Form extends Component {
  state = {
    TDate : null,
    Hage : null,
    MRT : null,
    CStore : null,
    Latitude : null,
    Longitude : null
  };

  onSubmit = (e) => {
    e.preventDefault();

    const data = this.state;

    console.log(data);

    // fetch('/predict', {
    //   method: 'POST',
    //   body: JSON.stringify({data}),
    //   headers: {"Content-type": "application/json; charset=UTF-8"}
    //   })
    //   .then(response => response.json()).then(message => 
    //   {console.log(message);});

  };

  onChange = (e) => {
    this.setState({ [e.target.id]: e.target.value});
  };

  render() {
    return (
      <div>
          <h2 className='title'>
            {this.props.title}
          </h2>
          <form className='form' onSubmit={this.onSubmit}>
              <label htmlFor='TDate'>Transaction Date: </label>
              <input type='date' id='TDate' onChange={this.onChange} />
  
              <label htmlFor='Hage'>House age: </label>
              <input type='number' id='Hage' step='.01' placeholder='0' onChange={this.onChange} />
  
              <label htmlFor='MRT'>Distance to the nearest Metro Station(Metres): </label>
              <input type='number' id='MRT' step='0.00001' placeholder='0' onChange={this.onChange} />
  
              <label htmlFor='CStore'>Convinience Store: </label>
              <input type='number' id='CStore' placeholder='0' onChange={this.onChange} />
  
              <label htmlFor='Latitude'>Latitude: </label>
              <input type='number' id='Latitude' step='0.00001' placeholder='0' onChange={this.onChange} />
  
              <label htmlFor='Longitude'>Longitude: </label>
              <input type='number' id='Longitude' step='0.00001' placeholder='0' onChange={this.onChange} />
  
              <input type='submit'  value='Predict' className='submit' />
          </form>
          <form className='answer'>
            <label htmlFor='Prediction'>Predicted Price: </label>
            <input type='text' id='Prediction' value='' placeholder='Predicted Price will appear here'/>
          </form>
      </div>
    )
  }
}

export default Form
