import React , {useState} from 'react'

  
state = {
  TDate : null,
  Hage : null,
  MRT : null,
  CStore : null,
  Latitude : null,
  Longitude : null
};



onSubmit = (e) => {
  e.preventDefault();

  const data = this.state;

  console.log(data);

  fetch('http://localhost:5000//predict', {
    method: 'POST',
    body: JSON.stringify({data}),
    headers: {'Content-type': 'application/json', 'Accept' : 'application/json'}
    })
    .then(response => response.json())
    .then(message => {console.log(message);})
    .catch(err => {console.log("Error Reading data " + err);});

};

onChange = (e) => {
  this.setState({ [e.target.id]: e.target.value});
};

const Form = ({title}) => {
  return (
    <div>
        <h2 className='title'>
          {title}
        </h2>
        <form className='form' >
            <label htmlFor='TDate'>Transaction Date: </label>
            <input type='date' id='TDate'  />

            <label htmlFor='Hage'>House age: </label>
            <input type='number' id='Hage' step='.01' placeholder='0'  />

            <label htmlFor='MRT'>Distance to the nearest Metro Station(Metres): </label>
            <input type='number' id='MRT' step='0.00001' placeholder='0'  />

            <label htmlFor='CStore'>Convinience Store: </label>
            <input type='number' id='CStore' placeholder='0'  />

            <label htmlFor='Latitude'>Latitude: </label>
            <input type='number' id='Latitude' step='0.00001' placeholder='0'  />

            <label htmlFor='Longitude'>Longitude: </label>
            <input type='number' id='Longitude' step='0.00001' placeholder='0'  />

            <input type='submit'  value='Predict' className='submit' />
        </form>
        <form className='answer'>
          <label htmlFor='Prediction'>Predicted Price: </label>
          <input type='text' id='Prediction' value='' placeholder='Predicted Price will appear here'/>
        </form>
    </div>
  ) 
}

export default Form
